    @Test
    void shouldSortList() {
        Day1 day1 = new Day1();

        List<Integer> sortedList1 = day1.sortList(input1);
        List<Integer> sortedList2 = day1.sortList(input2);

        assertEquals(Arrays.asList(1, 2, 3, 3, 3, 4), sortedList1);
        assertEquals(Arrays.asList(3, 3, 3, 4, 5, 9), sortedList2);
    }

    @Test
    void shouldCalculateDistance() {
        Day1 day1 = new Day1();

        int distance1 = day1.calculateDistance(3, 4);
        int distance2 = day1.calculateDistance(4, 3);

        assertEquals(1, distance1);
        assertEquals(1, distance2);
    }

    @Test
    void shouldSumDistances() {
        Day1 day1 = new Day1();

        int sum = day1.sumDistances(input1, input2);

        assertEquals(11, sum);
    }

    @Test
    void shouldSumFinalInput() {
        // Arrange
        Day1 day1 = new Day1();

        // Act
        int sum = day1.sumDistances(finalInput1, finalInput2);

        // Assert
        assertEquals(3569916, sum);
    }

    @Test
    void shouldCountHowManyTimesNumberIsInList() {
        Day1 day1 = new Day1();

        int count1 = day1.countHowManyTimesNumberIsInList(3, input1);
        int count2 = day1.countHowManyTimesNumberIsInList(1, input1);
        int count3 = day1.countHowManyTimesNumberIsInList(5, input1);

        assertEquals(3, count1);
        assertEquals(1, count2);
        assertEquals(0, count3);
    }

    @Test
    void shouldMultiplyNumberByCountOfAppearances() {
        Day1 day1 = new Day1();

        int result1 = day1.multiplyNumberByCountOfAppearances(3, input1);
        int result2 = day1.multiplyNumberByCountOfAppearances(1, input1);
        int result3 = day1.multiplyNumberByCountOfAppearances(5, input1);

        assertEquals(9, result1);
        assertEquals(1, result2);
        assertEquals(0, result3);
    }

    @Test
    void shouldCalculateResultFromTwoLists() {
        // Arrange
        Day1 day1 = new Day1();

        // Act
        int result = day1.processLists(input1, input2);

        // Assert
        assertEquals(31, result);
    }

    @Test
    void shouldCalculateFinalResultFromTwoLists() {
        // Arrange
        Day1 day1 = new Day1();

        // Act
        int result = day1.processLists(finalInput1, finalInput2);

        // Assert
        assertEquals(26407426, result);
    }
